import React from "react";
import Skeleton from '../components/PizzaBlog/Skeleton';
import PizzaBlog from '../components/PizzaBlog/';
import Categories from '../components/–°ategories';
import Sort from '../components/Sort';
import Pagination from '../components/Pagination'
import { list } from './../components/Sort'
import axios from 'axios'
import qs from 'qs'
import { useNavigate } from "react-router-dom";
import { SearchContext } from '../App'
import { useSelector, useDispatch } from 'react-redux'
import { setCategoryId, setCurrentPage, setParams } from './../Redux/Slices/filterSlice'
import { fetchPizzas } from '../Redux/Slices/pizzaSlice'

function Home() {
    const isSearch = React.useRef(false)
    const isMounter = React.useRef(false)
    //–∫–æ–Ω—Ç–µ–∫—Å—Ç
    const { searchValue } = React.useContext(SearchContext)
    //—Ö—É–∫–∏
    const navigate = useNavigate()
    const { sort, CategoryId, currentPage } = useSelector((state) => state.filterSlice)
    const dispatch = useDispatch()
    //–î–∞–Ω–Ω—ã–µ
    // const PizzasFilter = statePizzas.filter((obj) => obj.title.toLowerCase().includes(searchValue.toLowerCase().trim()) ? true : false)
    const { pizzas, status } = useSelector((state) => state.pizza)
    const Pizzas = pizzas.map((obj) => <PizzaBlog {...obj} key={obj.id.toString()} />)
    const SkeletonArr = [...new Array(6)].map((el, index) => < Skeleton key={index} />)
    //–∑–∞–ø—Ä–æ—Å—ã
    const order = sort.sortProperty.split('')[0] === '-' ? 'desc' : 'asc';
    const sortPut = sort.sortProperty.replace('-', '');
    const category = CategoryId > 0 ? `&category=${CategoryId}` : '';
    const search = searchValue ? `search=${searchValue}` : ''
    // –∑–∞–ø—Ä–æ—Å –Ω–∞ —Å–µ—Ä–≤–µ—Ä
    async function PizzasFetch() {
        dispatch(fetchPizzas({
            order,
            sortPut,
            category,
            search,
            currentPage
        }))
        window.scrollTo(0, 0)
    }
    // –µ—Å–ª–∏ –µ—Å—Ç—å URL –ø–∞—Ä—Å–∏–º –æ–±—å–µ–∫—Ç, –ø–µ—Ä–µ–¥–∞–µ–º –≤ —Å—Ç–µ–π—Ç, –¥–∞–µ–º –∑–∞–ø—Ä–æ—Å –Ω–∞ —Å–µ—Ä–≤–µ—Ä 
    React.useEffect(() => {
        if (window.location.search) {
            const params = qs.parse(window.location.search.substring(1))
            dispatch(setParams({
                ...params,
                sort: list.find((el) => el.sortProperty === params.sortProperty)
            }))
        }
        isSearch.current = true // –≥–æ–≤–æ—Ä–∏–º, —á—Ç–æ –¥–∏—Å–ø–∞—Ç—á –≤—ã–ø–æ–ª–Ω–∏–ª —Å–≤–æ—é —Ä–∞–±–æ—Ç—É
    }, [])

    //–µ—Å–ª–∏ –Ω–µ –±—ã–ª–æ –∑–∞–ø—Ä–æ—Å–∞ –ø–æ –≤—Ç–∞–Ω–æ–≤–ª–µ–Ω–æ–º—É URL ( —Ç–∞–∫ –∫–∞–∫ –≤—ã—à–µ —Ñ—É–Ω–∫—Ü–∏—è –Ω–µ –∏—Å–ø–æ–ª–Ω–∏–ª–∞—Å—å –∏ –Ω–µ –∏–∑–º–µ–Ω–∏–ª–∞ –Ω–∞ true)
    React.useEffect(() => {
        window.scrollBy(0, 0)
        // –≤—ã–ø–æ–ª–Ω—è–µ—Ç—Å—è —Ç–æ–ª–∫—å–æ –ø–æ—Å–ª–µ —Å–º–µ–Ω—ã isSearch.current = true 
        //  –µ—Å–ª–∏ –Ω–µ—Ç URL –≤—Å—Ç–∞–≤–ª–µ–Ω–æ–≥–æ, —Ç–æ –æ—Ç–ø—Ä–∞–≤–ª—è–µ–º –∑–∞–ø—Ä–æ—Å –ø–æ –¥–µ—Ñ–æ–ª—Ç—É —Å –∏–Ω–∏—à–µ–ª–°—Ç–µ–π—Ç
        if (!isSearch.current) {
            PizzasFetch()
        }

        isSearch.current = false

        // —è —Å–¥–µ–ª–∞–ª –ø–æ —Å–≤–æ–µ–º—É —Ç–∞–∫: —É –º–µ–Ω—è –ø–æ –¥—Ä—É–≥–æ–º—É –Ω–µ —Ä–∞–±–æ—Ç–∞–µ—Ç> –æ—Å—Ç–∞–≤–ª—é –Ω–∞ –≤—Å—è–∫–∏–π 
        // –µ—Å–ª–∏ –Ω–µ—Ç URL –≤—Å—Ç–∞–≤–ª–µ–Ω–æ–≥–æ, —Ç–æ –æ—Ç–ø—Ä–∞–≤–ª—è–µ–º –∑–∞–ø—Ä–æ—Å –ø–æ –¥–µ—Ñ–æ–ª—Ç—É —Å –∏–Ω–∏—à–µ–ª–°—Ç–µ–π—Ç
        if (window.location.search === '') {
            PizzasFetch()
        }

    }, [CategoryId, sort, currentPage, searchValue])
    // –µ—Å–ª–∏ –≤—Ç–æ—Ä–æ–π –∏ –±–æ–ª–µ–µ —Ä–µ–Ω–¥–µ—Ä–∏–Ω–≥ - –≤—Å—Ç–∞–≤–ª—è–µ–º URL –ø—Ä–∏–π–¥—è—â–∏–π —Å state
    React.useEffect(() => {
        if (isMounter.current) {
            const queryString = qs.stringify({
                sortProperty: sort.sortProperty,
                CategoryId,
                currentPage
            })
            navigate(`?${queryString}`)
        }
        isMounter.current = true
    }, [CategoryId, sort, currentPage])

    function set–°ategories(i) {
        dispatch(setCategoryId(i))
    } // –∏–ª–∏ —Å—Ä–∞–∑—É –º–æ–∂–Ω–æ –ø–µ—Ä–µ–¥–∞—Ç—å –≤ –ø—Ä–æ–ø—Å—ã —ç—Ç–æ—Ç –∫–æ–ª –±–µ–∫ 

    function onChangeCurrent(number) {
        dispatch(setCurrentPage(number))
    }

    return (
        <div className="container">
            <div>
                <div className="content__top">
                    <Sort />
                    <Categories value={CategoryId} onClickCategories={set–°ategories} />
                </div>
                <h2 className="content__title">–í—Å–µ –ø–∏—Ü—Ü—ã</h2>
                <div className="content__items">
                    {status === 'error' ?
                        <div>
                            <p>–ù–∏—á–µ–≥–æ –Ω–µ –Ω–∞–π–¥–µ–Ω–æ üòî </p>
                        </div> :
                        status === 'pending' ? SkeletonArr : Pizzas}


                </div>
            </div>
            <Pagination currentPage={currentPage} onChangeCurrent={onChangeCurrent} />
        </div>
    )
}

export default Home
